import React, { useContext, useState } from 'react'
import CartContext from '../../store/cart-context';
import Modal from '../UI/Modal';
import classes from "./Cart.module.css"
import CartItem from './CartItem';
import Checkout from './Checkout';
function Cart(props) {
    const[isCheckout,setIsCheckout]=useState(false);
    const [isSubmitting,setIsSubmitting]=useState(true);
    const [didSubmit,setDidSubmit]=useState(false);
    const cartCtx = useContext(CartContext);
    const totalAmount=cartCtx.totalAmount.toFixed(2);
    const hasItems=cartCtx.items.length>0;
    const cartItemRemoveHandler=id=>{cartCtx.removeItem(id);};
    const cartItemAddHandler=item=>{
        cartCtx.addItem({...item,amount:1})
    };
    const orderHandler=()=>{setIsCheckout(true)}
    const submitOrderHandler=async(userData)=>{
        setIsSubmitting(true);
        await fetch("https://food-app-b8b01-default-rtdb.asia-southeast1.firebasedatabase.app/order.json",{
        method:"POST",
        body:JSON.stringify({
            user:userData,
            orderedItems:cartCtx.items
        })
    });
        setIsSubmitting(false);
        setDidSubmit(true);
    }
    const cartItems=<ul className={classes["cart-items"]}>{cartCtx.items.map(item=><CartItem id={item.id} name={item.name} amount={item.amount} price={item.price} onRemove={cartItemRemoveHandler.bind(null,item.id)} onAdd={cartItemAddHandler.bind(null,item)}/>)}</ul>;
    const modalAction=<div className={classes.actions}>
    <button className={classes["button--alt"]} onClick={props.onClose}>Close</button>
    {hasItems && <button className={classes.button} onClick={orderHandler}>Order</button>}
</div>
  return (
    <Modal onClose={props.onClose}>
        {cartItems}
        <div className={classes.total}>
            <span>Total Amount</span>
            <span>${totalAmount}</span>
        </div>
        {isCheckout && <Checkout onConfirm={submitOrderHandler} onCancel={props.onClose} />}
        {!isCheckout && modalAction}

    </Modal>
  )
}

export default Cart
